const citacion=document.getElementById("citacion"),persona=document.getElementById("persona"),tweetButton=document.getElementById("tweet"),otraButton=document.getElementById("otra");let name,content,object,url="https://big-okra.glitch.me/https://quotesondesign.com/wp-json/posts?filter[orderby]=rand&filter[posts_per_page]=10&callback=",data=[],index=0;const getCitacion=()=>{object=data[index],name=`- <cite>${object.title}</cite>`,content=`"${content=(content=object.content).substr(3,content.length-8)}"`,persona.innerHTML=name,citacion.innerHTML=content},request=()=>{let t=new XMLHttpRequest;t.onreadystatechange=(()=>{4===t.readyState&&(data=JSON.parse(t.responseText),getCitacion())}),t.open("GET",url,!0),t.send()};tweetButton.addEventListener("click",()=>{let t=citacion.innerText+" "+persona.innerText;if(t.length>280)alert("Demasiado largo, no se puede twitear");else{var e="http://twitter.com/home?status="+encodeURIComponent(t);window.open(e,"_blank")}}),otraButton.addEventListener("click",()=>{index===data.length-1?(index=0,request()):(index+=1,getCitacion())}),request();